import React, { useEffect, useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom"; // Import useNavigate
import Button from "./Button";
import "./../styles/Navbar.css";

const Navbar = () => {
  const [scrolled, setScrolled] = useState(false);
  const location = useLocation(); // Get the current route location
  const navigate = useNavigate(); // Hook for programmatic navigation

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    // Only add scroll event listener if not on the About or Contact page
    if (location.pathname !== "/about" && location.pathname !== "/contact") {
      window.addEventListener("scroll", handleScroll);
    }

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [location.pathname]); // Re-run effect when the route changes

  // Define the logo URLs
  const defaultLogo =
    "https://10web-site.ai/219/wp-content/uploads/sites/231/2025/01/logo_0xiTKDaH.webp";
  const aboutLogo =
    "https://10web-site.ai/219/wp-content/uploads/sites/231/2025/01/logo_490xkFvp.webp";
  const contactLogo =
    "https://10web-site.ai/219/wp-content/uploads/sites/231/2025/01/logo_490xkFvp.webp"; // Replace with the actual URL of the About page logo

  // Function to handle the "Contact Us" button click
  const handleContactClick = () => {
    navigate("/contact"); // Navigate to the Contact page
  };

  return (
    <nav
      className={`navbar ${scrolled ? "navbar-scrolled" : ""} ${
        location.pathname === "/about" || location.pathname === "/contact"
          ? "navbar-about"
          : ""
      }`}
    >
      <Link to="/">
        <img
          width="150"
          height="40"
          src={
            location.pathname === "/about"
              ? aboutLogo
              : location.pathname === "/contact"
              ? contactLogo
              : defaultLogo
          }
          className="attachment-fullage-10290"
          alt="QuikChefs"
        />
      </Link>
      <ul className="nav-links">
        <li>
          <Link to="/about">About Us</Link>
        </li>
        <li>
          <a href="#services">Services</a>
        </li>
        <li>
          <a href="#portfolio">Portfolio</a>
        </li>
        <li>
          <Link to="/contact">Contact Us</Link>
        </li>
      </ul>
      <div className="buttons">
        <Button text="Our Services" type="btn-outline" />
        <Button
          text="Contact Us"
          type="btn-primary"
          onClick={handleContactClick} // Pass the click handler
        />
      </div>
    </nav>
  );
};

export default Navbar;
