import React, { useEffect, useState } from "react";
import { Link, useLocation } from "react-router-dom"; // Import useLocation
import Button from "./Button";
import './../styles/Navbar.css';

const Navbar = () => {
  const [scrolled, setScrolled] = useState(false);
  const location = useLocation(); // Get the current route location

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    // Only add scroll event listener if not on the About or Contact page
    if (location.pathname !== "/about" && location.pathname !== "/contact") {
      window.addEventListener("scroll", handleScroll);
    }

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [location.pathname]); // Re-run effect when the route changes

  // Define the logo URLs
  const defaultLogo =
    "https://10web-site.ai/219/wp-content/uploads/sites/231/2025/01/logo_0xiTKDaH.webp";
  const aboutLogo = "URL_TO_YOUR_ABOUT_PAGE_LOGO"; // Replace with the actual URL of the About page logo
  const contactLogo = "URL_TO_YOUR_CONTACT_PAGE_LOGO"; // Replace with the actual URL of the Contact page logo

  return (
    <nav
      className={`navbar ${scrolled ? "navbar-scrolled" : ""} ${
        location.pathname === "/about" || location.pathname === "/contact"
          ? "navbar-special"
          : ""
      }`}
    >
      <Link to="/">
        <img
          width="150"
          height="40"
          src={
            location.pathname === "/about"
              ? aboutLogo
              : location.pathname === "/contact"
              ? contactLogo
              : defaultLogo
          } // Dynamically change the logo
          className="attachment-fullage-10290"
          alt="QuikChefs"
        />
      </Link>
      <ul className="nav-links">
        <li>
          <Link to="/about">About Us</Link>
        </li>
        <li>
          <Link to="/contact">Contact Us</Link>
        </li>
      </ul>
      <div className="buttons">
        <Button text="Our Services" type="btn-outline" />
        <Button text="Contact Us" type="btn-primary" />
      </div>
    </nav>
  );
};

export default Navbar;
